<?phpclass Criador extends Controller {       private  $auth,$db;    public function acesso_restrito(){                  $this->auth = new AutenticaHelper();        $this->auth->setLoginControllerAction("Index", "")                   ->checkLogin("redirect");                      $this->db = new AdminModel();    }       public function criar_form(){        $this->acesso_restrito();          $acesso = new AcessoHelper();           $comando='/Criador/salvar_form/';        if($acesso->acesso_valida($comando)==true){            $acesso = new SessionHelper();            $menu = new MenuHelper("B", $Class, $AcaoForm, $MetodoDeEnvio);                  echo $menu->Menu();            $db = new Model();            $db->_tabela = 'information_schema.tables';                   $listar_tabela = $db->read(null, " table_schema = '$db->host_nome'", null, null, null, "TABLE_NAME AS id");                     $form = new FormularioHelper();            $inputs.= $form->select("Tipo","tipo", "col-md-3", array(array("id"=>"Model"),array("id"=>"Controller")),"id",);            $inputs.= $form->select("Tabela","tabela", "col-md-9",$listar_tabela,"id");            $inputs.= $form->Button("btn btn-rose", "Gerar");            $form->card("Construtor",$inputs,"col-md-12",$comando,"POST","engineering");        }else{            $this->view('error_permisao');        }     }    public function salvar_form(){        $this->acesso_restrito();                $acesso = new AcessoHelper();         $comando='/Criador/salvar_form/';                  if($acesso->acesso_valida($comando)==true){                            $tb=$_POST["tabela"];            $tipo=$_POST["tipo"];            $form = new FormHelper();            $form->criar_form($tb,$tipo);            $this->criar_form();        }else{            $this->view('error_permisao');        }     }  }  