<?php class Pareto extends Controller {       private  $auth,$db;    public function acesso_restrito(){                  $this->auth = new AutenticaHelper();        $this->auth->setLoginControllerAction('Index','')                   ->checkLogin('redirect');                      $this->db = new AdminModel();     }            public function pareto (){           $produto =  new ItensModel();           $produto->listar_Itens($join, $limit, "tabela = 'Venda'", $offset, $orderby, "id_produto, quantidade, valor_venda","id_produto");                    }    public function formulario(){            $this->acesso_restrito();        $acesso = new SessionHelper();      //  $listar_acesso=$acesso->selectSession('userAcesso');        $user_dados=$acesso->selectSession('userData');              $filial = new FilialModel();                  $status = new StatusModel();                $id_tabela= $this->getParams('id_tabela');        $tabela = $this->getParams('tabela');                $id = $this->getParams('id');        $comando="/Contato/incluir/";        $menu = new MenuHelper("Teste", $Class, $AcaoForm, $MetodoDeEnvio);        echo $menu->Menu($acesso->selectSession('userAcesso'));        $menu->Script('');                if(!empty($id)){            $contato = new ContatoModel();            $contato_dado = $contato->listar_contato(NULL,NULL,"id=$id AND id_status<>99",NULL,'Contato.id DESC');            $contato_dado=$contato_dado[0];            $comando="/Contato/alterar/";        }                        $form = new FormularioHelper("Cad","col-md-12 ",$comando."tabela/$tabela/id_tabela/$id_tabela/","POST");               $inputs.= $form->Input("hidden", "id", null, $id, $required,null);        $inputs.= $form->Input("hidden", "id_tabela", null, $id_tabela, $required,null);        $inputs.= $form->Input("hidden", "tabela", null, $tabela, $required,null);               $inputs.= $form->Input("text", "descricao", "col-md-8", $contato_dado["descricao"], $required,"Descricao");        $inputs.= $form->Input("text", "contato", "col-md-4", $contato_dado["contato"], $required,"Contato");        $inputs.= $form->select( "id_status", "col-md-6", $status->listar_Status(NULL,NULL,"id_status<>99",NULL,' Status.id DESC',NULL),"descricao");        $inputs.= $form->select( "id_filial", "col-md-6", $filial->listar_Filial(NULL,NULL,"id_status<>99",NULL,' Filial.id DESC',NULL),"nome_fantasia");        $inputs.= $form->Button("btn btn-rose  pull-right ","Salvar");        $form->card(__CLASS__,$inputs,"col-md-12",$comando,"POST","rule");        $form->Script('<script src="/web-files/js/core/jquery.min.js"></script>         <script src="/web-files/js/plugins/perfect-scrollbar.jquery.min.js"></script>');    } } ?> 