<?php class PedidoComposicao extends Controller {       private  $auth,$db;    public function acesso_restrito(){                  $this->auth = new AutenticaHelper();        $this->auth->setLoginControllerAction('Index','')                   ->checkLogin('redirect');                      $this->db = new AdminModel();     }         public function admin_listar(){        $this->acesso_restrito();        $acesso = new AcessoHelper();        $logs = new LogsModel();        $comando="/".__CLASS__."/incluir/";                 if($acesso->acesso_valida("/".__CLASS__."/admin_listar/")==true){             $filiais=$acesso->acesso_filial(__CLASS__);            $status= new StatusModel();            $filial = new FilialModel();                       $acesso = new SessionHelper();                       $pedido_composicao = new PedidoComposicaoModel();                      if(empty($_POST["pesquisa"])){$pesquisa=null; }else{ $pesquisa=$_POST["pesquisa"]; }            $menu = new MenuHelper("Bit a Bits", $Class, $AcaoForm, $MetodoDeEnvio);                    echo $menu->Menu();                        $form = new FormularioHelper(__CLASS__,"col-md-12" ,null,null,"people");                 $inputs = $form->Listar("col-md-12", null, "/".__CLASS__."/form/", $icone,$pedido_composicao->listar_PedidoComposicao(NULL,NULL,"id_status<>99 AND {$filiais}",NULL,' PedidoComposicao.id DESC',NULL,NULL,$pesquisa), "tabela1", array(array("acao"=>"/".__CLASS__."/form/","classe"=>"btn-sm btn-warning","icone"=>"edit"),array("acao"=>"/".__CLASS__."/visualizar/","classe"=>"btn-sm btn-rose","icone"=>"remove_red_eye"),array("acao"=>"/Logs/form/","classe"=>"btn-sm btn-danger","icone"=>"close")));            $form->card(__CLASS__,$inputs,"col-md-12",$comando,"POST","pending_actions");        }else{            $this->view('error_permisao');        }        } public function form(){         $this->acesso_restrito();        $acesso = new SessionHelper();        $menu = new MenuHelper("B", $Class, $AcaoForm, $MetodoDeEnvio);                echo $menu->Menu();              $filial = new FilialModel();                           $listar_filial = $filial->listar_Filial(NULL,NULL,"id_status<>99",NULL,' Filial.id DESC',NULL);                           $dados['listar_filial'] = $listar_filial;         $pedido = new PedidoModel();                           $listar_pedido = $pedido->listar_Pedido(NULL,NULL,"id_status<>99",NULL,' Pedido.id DESC',NULL);                           $dados['listar_pedido'] = $listar_pedido;         $composicao = new ComposicaoModel();                           $listar_composicao = $composicao->listar_Composicao(NULL,NULL,"id_status<>99",NULL,' Composicao.id DESC',NULL);                           $dados['listar_composicao'] = $listar_composicao;         $status = new StatusModel();                           $listar_status = $status->listar_Status(NULL,NULL,"id_status<>99",NULL,' Status.id ASC',NULL);                           $dados['listar_status'] = $listar_status;  $pedidocomposicao = new PedidoComposicaoModel();        $id = $this->getParams('id');        $dados['id']=$id;        if(!empty($id)){            $listar_pedidocomposicao = $pedidocomposicao->listar_pedidocomposicao(NULL,NULL,"id=$id AND id_status<>99",NULL,'PedidoComposicao.id DESC',NULL);            $dados['listar_pedidocomposicao'] = $listar_pedidocomposicao;        }         $this->view('form_pedidocomposicao',$dados);    } public function incluir(){            $this->acesso_restrito();        $acesso = new AcessoHelper();         $logs = new LogsModel();        $comando='/PedidoComposicao/incluir/';                    if($acesso->acesso_valida($comando)==true){            $pedidocomposicao = new PedidoComposicaoModel();                  $id=$pedidocomposicao->cadastrar_pedidocomposicao(                 array(                    'id_filial'=>$_POST['id_filial'],                    'id_pedido'=>$_POST['id_pedido'],                    'id_composicao'=>$_POST['id_composicao'],                    'tipo'=>$_POST['tipo'],                    'id_status'=>$_POST['id_status'],                    'data_lancamento'=>  date("Y-m-d H:i:s"),                )            );              $logs->cadastrar_logs($comando,$id);//Gera Logs            $redirect = new RedirectHelper();            $redirect->goToUrl('/PedidoComposicao/admin_listar/');            }else{            $this->view('error_permisao');        }    } public function alterar(){            $this->acesso_restrito();        $acesso = new AcessoHelper();         $logs = new LogsModel();        $comando='/PedidoComposicao/alterar/';        if($acesso->acesso_valida($comando)==true){            $id = $_POST['id'];            $pedidocomposicao = new PedidoComposicaoModel();                  $pedidocomposicao->alterar_pedidocomposicao(                array(                    'id_filial'=>$_POST['id_filial'],                    'id_pedido'=>$_POST['id_pedido'],                    'id_composicao'=>$_POST['id_composicao'],                    'tipo'=>$_POST['tipo'],                    'id_status'=>$_POST['id_status'],                    'data_lancamento'=>  date("Y-m-d H:i:s"),                ),'id='.$id            );              $logs->cadastrar_logs($comando,$id);//Gera Logs            $redirect = new RedirectHelper();            $redirect->goToUrl('/PedidoComposicao/admin_listar/');            }else{            $this->view('error_permisao');        }    }public function excluir(){            $this->acesso_restrito();        $acesso = new AcessoHelper();         $logs = new LogsModel();        $comando='/PedidoComposicao/excluir/';        if($acesso->acesso_valida($comando)==true){            $id = $this->getParams('id');            $pedidocomposicao = new PedidoComposicaoModel();                  $pedidocomposicao->excluir_pedidocomposicao( array( 'id_status'=>'99' ),'id='.$id );              $logs->cadastrar_logs($comando,$id);//Gera Logs            $redirect = new RedirectHelper();            $redirect->goToUrl('/PedidoComposicao/admin_listar/');            }else{            $this->view('error_permisao');        }    }  } ?> 