<?php    class AcessoModel extends Model{        public $_tabela='Acesso';        public function  listar_acesso($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_acesso(array $dados){            return $this->insert($dados);                                    }        public function  alterar_acesso(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_acesso(array $dados,$where){            return $this->update($dados, $where);                                    }    }