<?php    class AdministradoresModel extends Model{        public $_tabela='Administradores';        public function  listar_Administradores($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$camposfrom=null,$group=null){            return $this->read($join,$where, $limit, $offset,$orderby,$camposfrom,$group);                                    }        public function  cadastrar_Administradores(array $dados){            return $this->insert($dados);                                    }         public function  alterar_Administradores(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Administradores(array $dados,$where){            return $this->update($dados, $where);                                    }    }