<?php    class AnamnesesModel extends Model{        public $_tabela='Anamneses';        public function  listar_Anamneses($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Anamneses(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Anamneses(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Anamneses(array $dados,$where){            return $this->update($dados, $where);                                    }    }