<?php    class AtestadoModel extends Model{        public $_tabela='Atestado';        public function  listar_Atestado($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Atestado(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Atestado(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Atestado(array $dados,$where){            return $this->update($dados, $where);                                    }    }