<?php    class CaixaPModel extends Model{        public $_tabela='CaixaPai';        public function  listar_Caixa($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Caixa(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Caixa(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Caixa(array $dados,$where){            return $this->update($dados, $where);                                    }    }