<?php    class CargoModel extends Model{        public $_tabela='Cargo';        public function  listar_Cargo($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Cargo(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Cargo(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Cargo(array $dados,$where){            return $this->update($dados, $where);                                    }    }