<?php    class ChamadoGrupoInternoModel extends Model{//Interno Model//Externo         public $_tabela='ChamadoGrupo';         public function  listar_ChamadoGrupo($join=null,$limit=null,$where=null,$offset=null,$orderby=null){            return $this->read($join,$where, $limit, $offset,$orderby);                                    }        public function  cadastrar_ChamadoGrupo(array $dados){            return $this->insert($dados);                                    }        public function  alterar_ChamadoGrupo(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_ChamadoGrupo(array $dados,$where){            return $this->update($dados, $where);                                    }    }