<?php    class ComposicaoModel extends Model{        public $_tabela='Composicao';        public function  listar_Composicao($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Composicao(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Composicao(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Composicao(array $dados,$where){            return $this->update($dados, $where);                                    }    }