<?php    class ContaBancariaModel extends Model{        public $_tabela='ContaBancaria';        public function  listar_ContaBancaria($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_ContaBancaria(array $dados){            return $this->insert($dados);                                    }        public function  alterar_ContaBancaria(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_ContaBancaria(array $dados,$where){            return $this->update($dados, $where);                                    }    }