<?php    class ContasModel extends Model{        public $_tabela='Contas';        public function  listar_contas($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_contas(array $dados){            return $this->insert($dados);                                    }        public function  alterar_contas(array $dados,$where){            return $this->update($dados, $where);                                     }        public function  excluir_Contas(array $dados,$where){            return $this->update($dados, $where);                                    }    }