<?php    class ContatoModel extends Model{        public $_tabela='Contato';        public function  listar_Contato($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from);                                    }        public function  cadastrar_Contato(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Contato(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Contato(array $dados,$where){            return $this->update($dados, $where);                                    }                   }