<?php    class ContratoModel extends Model{        public $_tabela='Contrato';        public function  listar_Contrato($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Contrato(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Contrato(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Contrato(array $dados,$where){            return $this->update($dados, $where);                                    }    }