<?php    class EclesiasticoModel extends Model{        public $_tabela='Eclesiastico';        public function  listar_Eclesiastico($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Eclesiastico(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Eclesiastico(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Eclesiastico(array $dados,$where){            return $this->update($dados, $where);                                    }    }