<?php    class EnderecoModel extends Model{        public $_tabela='Endereco';        public function  listar_Endereco($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquia=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Endereco(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Endereco(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Endereco(array $dados,$where){            return $this->update($dados, $where);                                    }    }