<?php    class EscolaridadeModel extends Model{        public $_tabela='Escolaridade';        public function  listar_Escolaridade($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Escolaridade(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Escolaridade(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Escolaridade(array $dados,$where){            return $this->update($dados, $where);                                    }    }