<?php    class FilialModel extends Model{        public $_tabela='Filial';        public function  listar_Filial($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Filial(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Filial(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Filial(array $dados,$where){            return $this->update($dados, $where);                                    }                   }