<?php    class GrupoAcessoModel extends Model{        public $_tabela='GrupoAcesso';        public function  listar_GrupoAcesso($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_GrupoAcesso(array $dados){            return $this->insert($dados);                                    }        public function  alterar_GrupoAcesso(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_GrupoAcesso(array $dados,$where){            return $this->update($dados, $where);                                    }    }