<?php    class GrupoFilialModel extends Model{        public $_tabela='GrupoFilial';        public function  listar_GrupoFilial($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_GrupoFilial(array $dados){            return $this->insert($dados);                                    }        public function  alterar_GrupoFilial(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_GrupoFilial(array $dados,$where){            return $this->update($dados, $where);                                    }                   }