<?php    class IntranetModel extends ModelLiberacao{        public $_tabela='Intranet';        public function  listar_intranet($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_intranet(array $dados){            return $this->insert($dados);                                    }        public function  alterar_intranet(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_intranet(array $dados,$where){            return $this->update($dados, $where);                                    }    }