<?php    class LogsModel extends Model{        public $_tabela='Logs';        public function  listar_logs($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_logs(string $comando,string $id_comando,string $observacao=null){               $acesso = new SessionHelper();            $user_dados=$acesso->selectSession("userData");                          $contatoFilial = $acesso->selectSession('contatoFilial');            $contatoFilial=$contatoFilial[0]["contato"];            $exc= substr($comando, -8);            if($exc=="excluir/"){                 $email = new EmailHelper();            $email->enviar("$contatoFilial", "suporte@bitabits.com.br", "Bit a Bits", "ExclusÃ£o Sistema", "Foi Excluido o Registro $id_comando, $comando Motivo:$observacao", null, null,null);                            }                           $logs = new LogsModel();                        $logs->insert(array(                "comando" => $comando,                "id_comando"=> $id_comando,                "id_usuario"=> $user_dados["id"],                "id_status"=> '1',                                    "observacao"=>"$observacao",                "id_filial"=> $user_dados["id_filial"],                "data_lancamento" => date("Y-m-d H:i:s")            ));                                       }        public function  alterar_logs(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_logs(array $dados,$where){            return $this->update($dados, $where);                                    }    }