<?php    class MarcaModel extends Model{        public $_tabela='Marca';        public function  listar_Marca($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Marca(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Marca(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Marca(array $dados,$where){            return $this->update($dados, $where);                                    }    }