<?php    class MesaModel extends Model{        public $_tabela='Mesa';        public function  listar_Mesa($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Mesa(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Mesa(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Mesa(array $dados,$where){            return $this->update($dados, $where);                                    }    }