<?php    class ModeloModel extends Model{        public $_tabela='Modelo';        public function  listar_Modelo($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Modelo(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Modelo(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Modelo(array $dados,$where){            return $this->update($dados, $where);                                    }    }