<?php    class PedidoComposicaoModel extends Model{        public $_tabela='PedidoComposicao';        public function  listar_PedidoComposicao($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_PedidoComposicao(array $dados){            return $this->insert($dados);                                    }        public function  alterar_PedidoComposicao(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_PedidoComposicao(array $dados,$where){            return $this->update($dados, $where);                                    }    }