<?php    class PedidoModel extends Model{        public $_tabela='Pedido';        public function  listar_Pedido($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Pedido(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Pedido(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Pedido(array $dados,$where){            return $this->update($dados, $where);                                    }    }