<?php    class PessoaModel extends Model{        public $_tabela='Pessoa';        public function  listar_Pessoa($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Pessoa(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Pessoa(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Pessoa(array $dados,$where){            return $this->update($dados, $where);                                    }          }