<?php    class PlanoContasModel extends Model{        public $_tabela='PlanoContas';        public function  listar_PlanoContas($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_PlanoContas(array $dados){            return $this->insert($dados);                                    }        public function  alterar_PlanoContas(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_PlanoContas(array $dados,$where){            return $this->update($dados, $where);                                    }    }