<?php    class PontoModel extends Model{        public $_tabela='Ponto';        public function  listar_Ponto($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Ponto(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Ponto(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Ponto(array $dados,$where){            return $this->update($dados, $where);                                    }    }