<?php    class ProcedimentosModel extends Model{        public $_tabela='Procedimentos';        public function  listar_Procedimentos($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Procedimentos(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Procedimentos(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Procedimentos(array $dados,$where){            return $this->update($dados, $where);                                    }    }