<?php    class ProdutoModel extends Model{        public $_tabela='Produto';        public function  listar_Produto($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Produto(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Produto(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Produto(array $dados,$where){            return $this->update($dados, $where);                                    }    }