<?php            class ProgramaModel extends Model{        public $_tabela='Programa';        public function  listar_programa($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_programa(array $dados){            return $this->insert($dados);                                    }        public function  alterar_programa(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_programa(array $dados,$where){            return $this->update($dados, $where);                                    }    }