<?php    class PromocaoModel extends Model{        public $_tabela='Promocao';        public function  listar_Promocao($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Promocao(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Promocao(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Promocao(array $dados,$where){            return $this->update($dados, $where);                                    }    }