<?php    class ReceituarioModel extends Model{        public $_tabela='Receituario';        public function  listar_Receituario($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Receituario(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Receituario(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Receituario(array $dados,$where){            return $this->update($dados, $where);                                    }    }