<?php    class ReceiturarioModel extends Model{        public $_tabela='Receiturario';        public function  listar_Receiturario($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Receiturario(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Receiturario(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Receiturario(array $dados,$where){            return $this->update($dados, $where);                                    }    }