<?php    class RendaModel extends Model{        public $_tabela='Renda';        public function  listar_Renda($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Renda(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Renda(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Renda(array $dados,$where){            return $this->update($dados, $where);                                    }    }