<?php    class TipoDocumentoModel extends Model{        public $_tabela='TipoDocumento';        public function  listar_TipoDocumento($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_TipoDocumento(array $dados){            return $this->insert($dados);                                    }        public function  alterar_TipoDocumento(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_TipoDocumento(array $dados,$where){            return $this->update($dados, $where);                                    }    }