<?php    class TipoPagamentoModel extends Model{        public $_tabela='TipoPagamento';        public function  listar_TipoPagamento($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_TipoPagamento(array $dados){            return $this->insert($dados);                                    }        public function  alterar_TipoPagamento(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_TipoPagamento(array $dados,$where){            return $this->update($dados, $where);                                    }    }