<?php    class TutorialModel extends ModelLiberacao{         public $_tabela='Tutorial';        public function  listar_Tutorial($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Tutorial(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Tutorial(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Tutorial(array $dados,$where){            return $this->update($dados, $where);                                    }    } 