<?php    class VendaModel extends Model{        public $_tabela='Venda';        public function  listar_Venda($join=null,$limit=null,$where=null,$offset=null,$orderby=null,$from=null,$group=null,$pesquisa=null){            return $this->read($join,$where, $limit, $offset,$orderby,$from,$group,$pesquisa);                                    }        public function  cadastrar_Venda(array $dados){            return $this->insert($dados);                                    }        public function  alterar_Venda(array $dados,$where){            return $this->update($dados, $where);                                    }        public function  excluir_Venda(array $dados,$where){            return $this->update($dados, $where);                                    }    }