<?phpclass AcessoHelper {    public function acesso_valida(String $comando){        $acesso = new SessionHelper();        $listar_acessos=$acesso->selectSession("userAcesso");         $acao_acesso=false;        foreach ($listar_acessos as $acesso_comando){            if($acesso_comando["comando"]==$comando){                 $acao_acesso=true;            }        }        return $acao_acesso;    }    public function acesso_filial(String $tabela){        $acesso = new SessionHelper();        $listar_filial=$acesso->selectSession('userFilial');        $valores="$tabela.id_filial='0'";        foreach ($listar_filial as $acesso_filial){           $valores .= " OR $tabela.id_filial='".$acesso_filial['id_filial']."'";        }        return $valores;    }        }